const articleModel = require("./articleModel");
// import articleModel from "./articleModel";

async function createArticleInMongoDb(articleDetails) {
    try {
        const dataArticleCreatedinDb = await new articleModel(articleDetails).save();
        console.log(articleDetails, 'before');
        return dataArticleCreatedinDb;
    } catch {
        return null;
    }
}
async function getAllArticles() {
    try {
        const allArticles = await articleModel.find();
        return allArticles;
    } catch {
        return null;
    }
}

async function deleteOneArticle(id) {
    try {

        const deletedCard = await articleModel.deleteOne({
            _id: id
        });
        return deletedCard;
    } catch {
        return null;
    }
}






